mapper.update:
update client_specialty_record
client_number = #{clientNumber}, specialty_id = (select house_specialty.id as specialty_id from house_specialty join landlord_house where house_specialty.house_id= landlord_house.id and specialty_name =#{specialtyName} and house_name =#{houseName}), quantity = #{quantity}, price = #{price}, order_time = #{orderTime}, finish_time = #{finishTime}, is_received = #{isReceived}, is_paid = #{isPaid}, is_done = #{isDone},
where id = #{id}

update client_specialty_comment
specialty_record_id = #{specialtyRecordId}, score = #{score}, comment = #{comment}, photo = #{photo},
where id = #{id}
