select.where:
house_id in (select id from landlord_house where host_number=#{userId})
 and house_id = #{houseId}
 and specialty_name like concat('%', #{specialtyName}, '%')
 and description = #{description}
 and price = #{price}
 and inventory = #{inventory}
 and image_url = #{imageUrl}

mapper.update:
update house_specialty
house_id =IF(#{houseId} IN (select id from landlord_house where host_number = #{userId}),#{houseId},null), specialty_name = #{specialtyName}, description = #{description}, price = #{price}, inventory = #{inventory}, image_url = #{imageUrl},
where id = #{id}
